import { SAVE_FORM } from '../actions/action';

const INITIAL_STATE = {
  nome: '',
  email: '',
  cpf: '',
  endereco: '',
  cidade: '',
  estado: '',
  profissional: {
    curriculo: '',
    cargo: '',
    descricao: '',
  },
};

const formsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SAVE_FORM:
    return {
      ...state,
      information: {},
    };
  default:
    return state;
  }
};

export default formsReducer;
