import { SAVE_FORM } from '../actions/action';

const INITIAL_STATE = {
  information: {
    nome: '',
    email: '',
    cpf: '',
    endereco: '',
    cidade: '',
    estado: '',
    profissional: {
      curriculo: '',
      cargo: '',
      descricao: '',
    },
  }
};

const formsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SAVE_FORM:
    return {
      ...state,
      information: { 
    ...state.information, 
    nome: action.nome,
    email: action.email,
    cpf: action.cpf,
    endereco: action.endereco,
    cidade: action.cidade,
    estado: action.estado,
      },
    };
  default:
    return state;
  }
};

export default formsReducer;
